SELECT 

FROM t_ba_att a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.office_id = #{office.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_ba_att a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_ba_att SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

