mapper.select:
SELECT 

FROM t_ba_calendar a

WHERE  a.year = #{year}

SELECT 

FROM t_ba_calendar a


	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.year = #{year}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_ba_calendar a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
