mapper.select:
SELECT 

FROM t_ba_note_role a

WHERE a.id = #{id}

SELECT 

FROM t_ba_note_role a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_ba_note_role a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_ba_note_role SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
