mapper.select:
SELECT 

FROM t_ba_temp_activity a

WHERE a.id = #{id}

SELECT
FROM t_ba_temp_activity a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.activity_name LIKE '%'||#{activityName}||'%' '%'+#{activityName}+'%' concat('%',#{activityName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM t_ba_temp_activity a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_ba_temp_activity SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
	   SELECT 

         FROM t_ba_temp_activity a

        WHERE a.del_flag != 1

            AND a.activity_name = #{activityName}


            AND a.id != #{id}

SELECT 

  FROM t_ba_temp_activity a

 WHERE a.del_flag != 1

    AND a.logo = #{logo}


    AND a.id != #{id}
